export function registerDist(...args) {
    let peer: FluencePeer;
    let serviceId;
    let service;
    if (args[0] instanceof FluencePeer) {
        peer = args[0];
    } else {
        peer = FluencePeer.default;
    }

    if (typeof args[0] === 'string') {
        serviceId = args[0];
    } else if (typeof args[1] === 'string') {
        serviceId = args[1];
    } else {
        serviceId = 'dist';
    }

    if (!(args[0] instanceof FluencePeer) && typeof args[0] === 'object') {
        service = args[0];
    } else if (typeof args[1] === 'object') {
        service = args[1];
    } else {
        service = args[2];
    }

    peer.callServiceHandler.use((req, resp, next) => {
        if (req.serviceId !== serviceId) {
            next();
            return;
        }

        if (req.fnName === 'add_blueprint') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {
                    blueprint: req.tetraplets[0],
                },
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.add_blueprint(req.args[0], callParams);
        }

        if (req.fnName === 'add_module') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {
                    wasm_b56_content: req.tetraplets[0],
                    conf: req.tetraplets[1],
                },
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.add_module(req.args[0], req.args[1], callParams);
        }

        if (req.fnName === 'add_module_from_vault') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {
                    path: req.tetraplets[0],
                    config: req.tetraplets[1],
                },
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.add_module_from_vault(req.args[0], req.args[1], callParams);
        }

        if (req.fnName === 'default_module_config') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {
                    module_name: req.tetraplets[0],
                },
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.default_module_config(req.args[0], callParams);
        }

        if (req.fnName === 'get_interface') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {
                    module_id: req.tetraplets[0],
                },
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.get_interface(req.args[0], callParams);
        }

        if (req.fnName === 'list_blueprints') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {},
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.list_blueprints(callParams);
        }

        if (req.fnName === 'list_modules') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {},
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.list_modules(callParams);
        }

        if (req.fnName === 'make_blueprint') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {
                    name: req.tetraplets[0],
                    dependencies: req.tetraplets[1],
                },
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.make_blueprint(req.args[0], req.args[1], callParams);
        }

        if (req.fnName === 'make_module_config') {
            const callParams = {
                ...req.particleContext,
                tetraplets: {
                    name: req.tetraplets[0],
                    mem_pages_count: req.tetraplets[1],
                    logger_enabled: req.tetraplets[2],
                    preopened_files: req.tetraplets[3],
                    envs: req.tetraplets[4],
                    mapped_dirs: req.tetraplets[5],
                    mounted_binaries: req.tetraplets[6],
                    logging_mask: req.tetraplets[7],
                },
            };
            resp.retCode = ResultCodes.success;
            resp.result = service.make_module_config(
                req.args[0],
                req.args[1],
                req.args[2],
                req.args[3],
                req.args[4],
                req.args[5],
                req.args[6],
                req.args[7],
                callParams,
            );
        }

        next();
    });
}

// Functions

export async function viaArr(
    node_id: string,
    viaAr: string[],
    config?: { ttl?: number },
): Promise<{ external_addresses: string[] }>;
export async function viaArr(
    peer: FluencePeer,
    node_id: string,
    viaAr: string[],
    config?: { ttl?: number },
): Promise<{ external_addresses: string[] }>;
export async function viaArr(...args) {
    let peer: FluencePeer;
    let node_id;
    let viaAr;
    let config;
    if (args[0] instanceof FluencePeer) {
        peer = args[0];
        node_id = args[1];
        viaAr = args[2];
        config = args[3];
    } else {
        peer = FluencePeer.default;
        node_id = args[0];
        viaAr = args[1];
        config = args[2];
    }

    let request: RequestFlow;
    const promise = new Promise<{ external_addresses: string[] }>((resolve, reject) => {
        const r = new RequestFlowBuilder()
            .disableInjections()
            .withRawScript(
                `
     (xor
 (seq
  (seq
   (seq
    (seq
     (seq
      (seq
       (seq
        (seq
         (call %init_peer_id% ("getDataSrv" "-relay-") [] -relay-)
         (call %init_peer_id% ("getDataSrv" "node_id") [] node_id)
        )
        (call %init_peer_id% ("getDataSrv" "viaAr") [] viaAr)
       )
       (call -relay- ("op" "noop") [])
      )
      (fold viaAr -via-peer-
       (seq
        (call -via-peer- ("op" "noop") [])
        (next -via-peer-)
       )
      )
     )
     (xor
      (call node_id ("peer" "identify") [] p)
      (seq
       (seq
        (seq
         (fold viaAr -via-peer-
          (seq
           (call -via-peer- ("op" "noop") [])
           (next -via-peer-)
          )
         )
         (call -relay- ("op" "noop") [])
        )
        (call %init_peer_id% ("errorHandlingSrv" "error") [%last_error% 1])
       )
       (call -relay- ("op" "noop") [])
      )
     )
    )
    (fold viaAr -via-peer-
     (seq
      (call -via-peer- ("op" "noop") [])
      (next -via-peer-)
     )
    )
   )
   (call -relay- ("op" "noop") [])
  )
  (xor
   (call %init_peer_id% ("callbackSrv" "response") [p])
   (call %init_peer_id% ("errorHandlingSrv" "error") [%last_error% 2])
  )
 )
 (call %init_peer_id% ("errorHandlingSrv" "error") [%last_error% 3])
)

                 `,
            )
            .configHandler((h) => {
                h.on('getDataSrv', '-relay-', () => {
                    return peer.connectionInfo.connectedRelays[0];
                });
                h.on('getDataSrv', 'node_id', () => {
                    return node_id;
                });
                h.on('getDataSrv', 'viaAr', () => {
                    return viaAr;
                });
                h.onEvent('callbackSrv', 'response', (args) => {
                    const [res] = args;
                    resolve(res);
                });

                h.onEvent('errorHandlingSrv', 'error', (args) => {
                    const [err] = args;
                    reject(err);
                });
            })
            .handleScriptError(reject)
            .handleTimeout(() => {
                reject('Request timed out for viaArr');
            });
        if (config && config.ttl) {
            r.withTTL(config.ttl);
        }
        request = r.build();
    });
    await peer.initiateFlow(request!);
    return promise;
}

